func Test_RlpEncodingIBFT(t *testing.T) {
	testBlock := `{"difficulty": "0x1","extraData": "","gasLimit": "0x4A6CE7","gasUsed": "0x0","hash": "0xed607d816f792bff503fc01bf8903b50aae5bbc6d00293350e38bba92cde40ab","logsBloom": "","miner": "0x955425273ef777d6430d910f9a8b10adbe95fff6","mixHash": "0x63746963616c2062797a616e74696e65206661756c7420746f6c6572616e6365","nonce": "0x0000000000000000","number": "0x26","parentHash": "0x6893c6fe9270461992e748db2f30aa1359babbd74d0392eb4c3476ef942eb5ec","receiptsRoot": "0x56e81f171bcc55a6ff8345e692c0f86e5b48e01b996cadc001622fb5e363b421","sha3Uncles": "0x1dcc4de8dec75d7aab85b567b6ccd41ad312451b948a7413f0a142fd40d49347","size": 901,"stateRoot": "0x4e64a3b5ab9c561f72836209e376d035a0aa23a1fc7251e5d21c3c8437fef58e","timestamp": "0x5C61902F","totalDifficulty": "0x27","transactions": [],"transactionsRoot": "0x56e81f171bcc55a6ff8345e692c0f86e5b48e01b996cadc001622fb5e363b421","uncles": []}`

	expectedProposal := ""

	expectedCommit := ""

	expectedSeals := ``

	// Marshall fake block into the Header
	var block *types.Header
	err := json.Unmarshal([]byte(testBlock), &block)
	if err != nil {
		fmt.Println(err)
	}

	// get the proposal and commit blocks and the commit seal array
	proposal, commit, seals := RlpEncodeIBFT(block)

	assert.Equal(t, expectedProposal, hex.EncodeToString(proposal))
	assert.Equal(t, expectedCommit, hex.EncodeToString(commit))
	assert.Equal(t, expectedSeals, hex.EncodeToString(seals))

}
